const burgerOpen=()=>{const t=document.querySelector(".header__bg"),c=document.querySelector(".burger .burger__item"),r=document.querySelector(".nav");c&&(c.addEventListener("click",e=>{t.classList.toggle("active"),r.classList.toggle("active"),e.currentTarget.classList.toggle("active")}),t.addEventListener("click",e=>{c.classList.remove("active"),r.classList.remove("active"),e.currentTarget.classList.remove("active")}))};burgerOpen();